    void getProductCategory(){
        RequestQueue queue = Volley.newRequestQueue(this);
        StringRequest stringRequest = new StringRequest(Request.Method.GET, Constants.GET_CATEGORIES_URL, new Response.Listener<String>() {
            @Override
            public void onResponse(String response) {
                try {
                    JSONObject mainObj = new JSONObject(response);
                    if(mainObj.getString("status").equals("success")){
                        JSONArray categoryArray = mainObj.getJSONArray("categories");

                        for(int i=0; i<categoryArray.length()-1; i++){

                            JSONObject object = categoryArray.getJSONObject(i);
                            ProductCategory productCategory = new ProductCategory(object.getInt("id"),object.getString("name"), Constants.CATEGORIES_IMAGE_URL+object.getString("icon"),
                                    object.getString("brief"),"#FFFFFF");

                            productCategoryArrayList.add(productCategory);
                        }

                        productCategoryAdapter.notifyDataSetChanged();


                    }else{

                    }
                } catch (JSONException e) {
                    e.printStackTrace();
                }
//                Log.e("err", response);
            }
        }, new Response.ErrorListener() {
            @Override
            public void onErrorResponse(VolleyError error) {
//                Log.d("erroeone", "mssg", error);
            }
        });

        queue.add(stringRequest);
    }

    void getProducts(){
        RequestQueue queue = Volley.newRequestQueue(this);
        String url = Constants.GET_PRODUCTS_URL + "?count=10";
        StringRequest stringRequest = new StringRequest(Request.Method.GET, "https://tutorials.mianasad.com/ecommerce/services/listProduct?count=10", new Response.Listener<String>() {
            @Override
            public void onResponse(String response) {
                try {
                    JSONObject mainObject = new JSONObject(response);

                    if(mainObject.getString("status").equals("success")) {
                        JSONArray productArrayfromInternet = mainObject.getJSONArray("products");

                        for(int i = 0; i<productArrayfromInternet.length()-1; i++){
                            Log.e("eee", response);
                            JSONObject obj = productArrayfromInternet.getJSONObject(i);
                            ProductDetail productDetail = new ProductDetail(obj.getString("name"), Constants.PRODUCTS_IMAGE_URL+ obj.getString("image"),
                                    obj.getString("status"), obj.getDouble("price"),obj.getDouble("price_discount"),
                                    obj.getInt("stock"), obj.getInt("id"));

                            productArrayList.add(productDetail);
                        }
                        productAdapter.notifyDataSetChanged();


                    }else{

                    }


                } catch (JSONException e) {
                    e.printStackTrace();
                }


            }
        }, new Response.ErrorListener() {
            @Override
            public void onErrorResponse(VolleyError error) {

            }
        });

        queue.add(stringRequest);
    }
